$$
\mathrm{Sn}^{4+}+4 \mathrm{e}^{-} \longrightarrow \mathrm{Sn} \quad \mathrm{E}_{2}^{0}=0.010 \mathrm{~V}
$$
<br/><br/>
$$
\begin{aligned}
& \mathrm{Sn}^{4+}+2 \mathrm{e}^{-} \longrightarrow \mathrm{Sn}^{2+} \quad \mathrm{E}_{\text {cell }}^{0} \\\\
& \mathrm{E}_{\mathrm{cell}}^{\mathrm{O}}=\frac{\mathrm{n}_{2} \mathrm{E}_{2}^{\mathrm{o}}+\mathrm{n}_{1} \mathrm{E}_{1}^{0}}{\mathrm{n}}=\frac{4(0.010)+2(0.140)}{2} \\\\
& \mathrm{E}_{\text {cell }}^{0}=0.16 \mathrm{~V}=16 \times 10^{-2} \mathrm{~V}
\end{aligned}
$$

$$
\begin{aligned}
\mathrm{P} & =\frac{e^2}{\mathrm{R}} \\\\
e & =\text { induced emf }=\frac{\mathrm{N} d \phi_B}{d t}=\mathrm{NA} \frac{d \mathrm{~B}}{d t}
\end{aligned}
$$
<br/><br/>$$
\text { and } \mathrm{R}=\frac{e l}{\pi r^2} \text { where } r \text { is the radius of the wire }
$$
<br/><br/>$$
\begin{array}{ll} 
& \mathrm{P}=\frac{\left(\mathrm{NA} \frac{d \mathrm{~B}}{d t}\right)^2}{\frac{e l}{\pi r^2}} \\\\
\Rightarrow & \mathrm{P} \propto \mathrm{N}^2 r^2 \\\\
\Rightarrow & \mathrm{P}^{\prime} \propto\left(\frac{\mathrm{N}}{2}\right)^2(2 r)^2 \\\\
\Rightarrow & \mathrm{P}^{\prime}=\mathrm{P}
\end{array}
$$
